 | grep -v grep | awk \'{print \"kill -9 \" $2}\'|sh\n\nredis_start(){\n        command=`ps -ef | grep node | grep -v grep`\n   if [ \"$command\" != \"\" ]\n   then\n         echo \"ycism exists, process is already running or crashed\"\n   else\n         echo \"Starting Ycism server...\"\n                        cd $YC_DIR\n         nohup $EXEC run serve &gt;&gt; /var/log/ycism.log 2&gt;&amp;1 &amp;\n         echo \"Ycism stated is successful...\"\n   fi\n}\nredis_stop(){\n        command=`ps -ef | grep node | grep -v grep`\n   if [ \"$command\" == \"\" ]\n   then\n         echo \"Ycism does not exist, process is not running\"\n   else\n         echo \"Stopping ...\"\n                        ps -ef | grep node | grep -v grep | awk \'{print \"kill -9 \" $2}\'| sh\n         echo \"Ycism stopped\"\n   fi\n}\n\n\ncase \"$1\" in\n    start)\n      redis_start\n        ;;\n    stop)\n        redis_stop\n        ;;\n   restart|reload)\n      redis_stop\n      sleep 0.3\n      redis_start\n      ;;\n    *)\n        echo \"Please use start or stop as first argument\"\n        ;;\nesac</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:paragraph -->\n<p>1.2        chmod +x /etc/init.d/runnpm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>2. 添加到系统服务</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>2.1       chkconfig --add  /etc/init.d/runnpm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.2       chkconfig  --level  2345 runnpm  on</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>3. 设置开机启动</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>3.1   systemctl enable runnpm       # 开机自启</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.2   systemctl start runnpm          # 启动服务</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.3   systemctl  stop  runnpm        # 停止服务</p>\n<!-- /wp:paragraph -->','centos-shell启动脚本','','publish','open','open','','centos-shell%e5%90%af%e5%8a%a8%e8%84%9a%e6%9c%ac','','','20