is_start\n      ;;\n    *)\n        echo \"Please use start or stop as first argument\"\n        ;;\nesac</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:paragraph -->\n<p>1.2        chmod +x /etc/init.d/runnpm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>2. 添加到系统服务</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>2.1       chkconfig --add  /etc/init.d/runnpm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.2       chkconfig  --level  2345 runnpm  on</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>3. 设置开机启动</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>3.1   systemctl enable runnpm       # 开机自启</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.2   systemctl start runnpm          # 启动服务</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.3   systemctl  stop  runnpm        # 停止服务</p>\n<!-- /wp:paragraph -->','centos-shell启动脚本','','inherit','closed','closed','','230-revision-v1','','','2019-07-23 09:31:14','2019-07-23 01:31:14','',230,'http://long521.cn/2019/07/23/230-revision-v1/',0,'revision','',0),(232,1,'2019-07-23 09:32:01','2019-07-23 01:32:01','<!-- wp:list -->\n<ul><li>编写shell脚本</li><li>添加到systemctl</li><li>设置开机启动</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>1. 编写shell可执行脚本</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>1.1            vim /etc/init.d/runnpm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">#!/bin/sh\n# chkconfig: 2345 56 26\n# description: NPM Service\n\n### BEGIN INIT INFO\n# Provides:          NPM\n# Required-Start:    $all\n# Required-Stop:     $all\n# Default-Start:     2 3 4 5\n# Default-Stop:      0 1 6\n# Short-Description: starts npm server\n# Description:       starts the test server by npm\n### END INIT INFO\n\n# Simple Redis init.d script conceived to work on Linux systems\n# as it does use of the /proc filesystem.\n\nEXEC=/usr/bin/npm\nYC_DIR=/media/sf_code/crm.fe.ycism.com/ycism\n\n# ps -ef | grep node | grep -v grep | awk \'{print \"kill -9 \" $2}\'|sh\n\nredis_start(){\n        command=`ps -ef | grep node | grep -v grep`\n   if [ \"$command\" != \"\" ]\n   then\n         echo \"ycism exists, process is already running or crashed\"\n   else\n         echo \"Starting Ycism server...\"\n                        cd $YC_DIR\n         nohup $EXEC run serve &gt;&gt; /var/log/ycism.log 2&gt;&amp;1 &amp;\n         echo \"Ycism stated is successful...\"\n   fi\n}\nredis_stop(){\n        command=`ps -ef | grep node | grep -v grep`\n   if [ \"$command\" == \"\" ]\n   then\n         echo \"Ycism does not exist, process is not running\"\n   else\n         echo \"Stopping ...\"\n                        ps -ef | grep node | grep -v grep | awk \'{print \"kill -9 \" $2}\'| sh\n         echo \"Ycism stopped\"\n   fi\n}\n\n\ncase \"$1\" in\n    start)\n      redis_start\n        ;;\n    stop)\n        redis_stop\n        ;;\n   restart|reload)\n      redis_stop\n      sleep 0.3\n      redis_start\n      ;;\n    *)\n        echo \"Please use start or stop as first argument\"\n        ;;\nesac</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:paragraph -->\n<p>1.2        chmod +x /etc/init.d/runnpm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>2. 添加到系统服务</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>2.1       chkconfig --add  /etc/init.d/runnpm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.2       chkconfig  --level  2345 runnpm  on</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>3. 设置开机启动</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>3.1   systemctl enable runnpm       # 开机自启</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.2   systemctl start runnpm          # 启动服务</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.3   systemctl  stop  runnpm        # 停止服务</p>\n<!-- /wp:paragraph -->','centos-shell启动脚本','','inherit','closed','closed','','230-revision-v1','','','2019-07-23 09:32:01','2019-07-23 01:32:01','',230,'http://long521.cn/2019/07/23/230-revision-v1/',0,'revision','',0),(233,1,'2019-07-23 12:29:58','2019-07-23 04:29:58','<!-- wp:list {\"ordered\":true} -->\n<ol><li>mysql表设计</li><li>索引设计</li><li>模型查询</li><li>控制器调用</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>1. mysql表设计</h4>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">1.1  组织表 （分类表）\n-------------------------------------------------\n CREATE TABLE <code>organizations</code> (\n   <code>id</code> int(10) unsigned NOT NULL AUTO_INCREMENT,\n   <code>parent_id</code> int(11) unsigned NOT NULL DEFAULT \'0\',\n   <code>name</code> varchar(100) NOT NULL,\n   <code>en_name</code> varchar(100) NOT NULL DEFAULT \'\',\n   <code>description</code> varchar(100) NOT NULL DEFAULT \'\',\n   <code>icon_style</code> varchar(191) NOT NULL DEFAULT \'\' COMMENT \'图标style\',\n   <code>level</code> tinyint(2) unsigned NOT NULL DEFAULT \'0\' COMMENT \'级别\',\n   <code>path</code> varchar(255) NOT NULL COMMENT \'组织路径用于快速查询\',\n   <code>created_at</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   <code>updated_at</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   PRIMARY KEY (<code>id</code>),\n   UNIQUE KEY <code>name</code> (<code>name</code>) USING BTREE,\n   KEY <code>path</code> (<code>path</code>)\n ) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 \n\n1.2  用户表 \n----------------------------------------------------\nCREATE TABLE <code>users</code> (\n   <code>id</code> int(10) unsigned NOT NULL AUTO_INCREMENT,\n   <code>username</code> varchar(191) NOT NULL,\n   <code>phone</code> varchar(191) NOT NULL,\n   <code>name</code> varchar(191) NOT NULL,\n   <code>email</code> varchar(191) NOT NULL,\n   <code>password</code> varchar(191) NOT NULL,\n   <code>created_at</code> timestamp NULL DEFAULT NULL,\n   <code>updated_at</code> timestamp NULL DEFAULT NULL,\n   PRIMARY KEY (<code>id</code>),\n   UNIQUE KEY <code>users_username_unique</code> (<code>username</code>)\n ) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4\n\n1.3  用户组织关系表 （中间表）\n-------------------------------------------------------\nCREATE TABLE <code>org_user</code> (\n   <code>user_id</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   <code>org_id</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   UNIQUE KEY <code>user-org</code> (<code>user_id</code>,<code>org_id</code>) USING BTREE\n ) ENGINE=MyISAM DEFAULT CHARSET=utf8</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>2. laravel模型构建</h4>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">2.1  OrganizationModel\n----------------------------------------\n<strong>&lt;?php\n\nnamespace </strong>App\\Models;\n\n<strong>class </strong>OrganizationModel <strong>extends </strong>BaseModel\n{\n    <em>// 与模型关联的数据表 string\n    </em><strong>protected $table </strong>= <strong>\'organizations\'</strong>;\n\n    <em>// 模型的日期字段的存储格式 参考date()函数参一说明 string\n    </em><strong>protected $dateFormat </strong>= <strong>\'U\'</strong>; <em>// U 秒时间戳\n\n    // 批量赋值黑名单\n    </em><strong>protected $guarded </strong>= [<strong>\'id\'</strong>, <strong>\'created_at\'</strong>, <strong>\'updated_at\'</strong>];\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>处理组织分类树\n     * </em><strong><em>@param </em></strong><em>array $list\n     * </em><strong><em>@param </em></strong><em>string $pk\n     * </em><strong><em>@param </em></strong><em>string $pid\n     * </em><strong><em>@param </em></strong><em>string $child\n     * </em><strong><em>@param </em></strong><em>int $root\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public static function </strong>tree($list=[], $pk=<strong>\'id\'</strong>, $pid = <strong>\'parent_id\'</strong>, $child = <strong>\'_child\'</strong>, $root = 0)\n    {\n        <em>// 创建Tree\n        </em>$tree = <strong>array</strong>();\n        <strong>if</strong>(<em>is_array</em>($list) &amp;&amp; $list) {\n            <em>// 创建基于主键的数组引用\n            </em>$refer = <strong>array</strong>();\n            <strong>foreach </strong>($list <strong>as </strong>$key => $data) {\n                $refer[$data[$pk]] =&amp; $list[$key];\n            }\n            <strong>foreach </strong>($list <strong>as </strong>$key => $data) {\n                <em>// 判断是否存在parent\n                </em>$parentId =  $data[$pid];\n                <strong>if </strong>($root == $parentId) {\n                    $tree[] =&amp; $list[$key];\n                }<strong>else</strong>{\n                    <strong>if </strong>(<strong>isset</strong>($refer[$parentId])) {\n                        $parent =&amp; $refer[$parentId];\n                        $parent[$child][] =&amp; $list[$key];\n                    }\n                }\n            }\n        }\n        <strong>return </strong>$tree;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>获取所有子级组织\n     * </em><strong><em>@param </em></strong><em>int $org || organization model\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public static function </strong>getAllChild($org=1){\n        <strong>if</strong>(<em>is_int</em>($org))\n            $org = <strong>self</strong>::<em>findOrFail</em>($org);\n\n        <strong>return self</strong>::<em>where</em>(<strong>\'path\'</strong>,<strong>\'like\'</strong>,$org-><strong>path </strong>. $org-><strong>id </strong>. <strong>\'-%\'</strong>)->get();\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>获取所有子级组织id\n     * </em><strong><em>@param </em></strong><em>int $org || organization model\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public static function </strong>getAllChildId($org=1){\n        <strong>return self</strong>::<em>getAllChild</em>($org)->pluck(<strong>\'id\'</strong>);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>获取组织和用户关系\n     * </em><strong><em>@param </em></strong><em>int $org || organization model\n     * </em><strong><em>@return </em></strong><em>User[]|\\Illuminate\\Database\\Eloquent\\Collection\n     */\n    </em><strong>public static function </strong>getOrgHaveUsers($org=1){\n        <em>// 1. 获取所有子组织id和本身id\n        </em>$self_id = <em>is_int</em>($org)? $org : $org-><strong>id</strong>;\n        $org_ids = <strong>self</strong>::<em>getAllChildId</em>($org)->push($self_id)->toArray();\n        $org_users = OrgUserModel::<em>whereIn</em>(<strong>\'org_id\'</strong>,$org_ids)->pluck(<strong>\'user_id\'</strong>)->unique();\n\n        <em>// 2. 获取所有用户\n        </em>$users = User::<em>all</em>([<strong>\'id\'</strong>,<strong>\'username\'</strong>,<strong>\'name\'</strong>]);\n\n        <em>// 3. 处理组织和用户绑定关系\n        </em>$users->transform(<strong>function </strong>($item)<strong>use</strong>($org_users){\n            $item[<strong>\'checked\'</strong>] = ( $org_users->search($item[<strong>\'id\'</strong>]) === <strong>false </strong>)?<strong>false</strong>:<strong>true</strong>;\n            <strong>return </strong>$item;\n        });\n\n        <strong>return </strong>$users;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>通过组织获取所属用户模型\n     * </em><strong><em>@param </em></strong><em>int $org || Organization model\n     * </em><strong><em>@return </em></strong><em>mixed\n     */\n    </em><strong>public static function </strong>getAllUsersByOrg($org=1){\n        <em>// 1. 获取所有属于该组织树下的user_id\n        </em>$user_ids = <strong>self</strong>::<em>getAllUserIdsByOrg</em>($org);\n\n        <em>// 2. 获取所有属于该组织下的用户模型\n        </em>$users = User::<em>whereIn</em>(<strong>\'id\'</strong>,$user_ids)->get();\n\n        <strong>return </strong>$users;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>通过组织获取所属用户id数组\n     * </em><strong><em>@param </em></strong><em>int $org || Organization model\n     * </em><strong><em>@return </em></strong><em>array $user_ids\n     */\n    </em><strong>public static function </strong>getAllUserIdsByOrg($org=1){\n        <em>// 1. 获取所有子组织id和本身id\n        </em>$self_id = <em>is_int</em>($org)? $org : $org-><strong>id</strong>;\n        $org_ids = <strong>self</strong>::<em>getAllChildId</em>($org)->push($self_id)->toArray();\n\n        <em>// 2. 获取所有属于该组织树下的user_id\n        </em>$user_ids = OrgUserModel::<em>whereIn</em>(<strong>\'org_id\'</strong>,$org_ids)->pluck(<strong>\'user_id\'</strong>)->unique()->toArray();\n\n        <strong>return </strong>$user_ids;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>给组织绑定用户\n     * </em><strong><em>@param </em></strong><em>$org_id\n     * </em><strong><em>@param </em></strong><em>array $users\n     * </em><strong><em>@return </em></strong><em>bool|mixed\n     */\n    </em><strong>public static function </strong>bindUsers($org_id,$users=[]){\n        <em>// 1. 用户为空直接删除并返回\n        </em><strong>if</strong>(!$users){\n            $res = OrgUserModel::<em>where</em>(<strong>\'org_id\'</strong>,$org_id)->delete();\n            <strong>return </strong>$res;\n        }\n\n        <em>// 2. 获取该组织树下原绑定用户\n        </em>$origin_bind_users = <strong>self</strong>::<em>getAllUserIdsByOrg</em>($org_id);\n\n        <em>// 3. 处理需要解绑与绑定的用户关系\n        </em>$intersect = <em>array_intersect</em>($origin_bind_users,$users);\n        $need_delete = <em>array_diff</em>($origin_bind_users,$intersect);\n        $need_add = <em>array_diff</em>($users,$intersect);\n        $insert_data = [];\n        <strong>foreach </strong>($need_add <strong>as </strong>$val){\n            $insert_data[] = [<strong>\'org_id\'</strong>=>$org_id,<strong>\'user_id\'</strong>=>$val];\n        }\n\n        <em>// 4. 执行操作\n        </em>$res = OrgUserModel::<em>where</em>(<strong>\'org_id\'</strong>,$org_id)->whereIn(<strong>\'user_id\'</strong>,$need_delete)->delete();\n        <strong>if</strong>($insert_data) $res = OrgUserModel::<em>insert</em>($insert_data);\n\n        <strong>return </strong>$res;\n    }\n\n\n}\n\n2.2  OrgUserModel\n----------------------------------------\n<strong>&lt;?php\n\nnamespace </strong>App\\Models;\n\n<strong>class </strong>OrgUserModel <strong>extends </strong>BaseModel\n{\n    <em>// 与模型关联的数据表 string\n    </em><strong>protected $table </strong>= <strong>\'org_user\'</strong>;\n\n    <em>// 该模型是否被自动维护时间戳 bool\n    </em><strong>public $timestamps </strong>= <strong>false</strong>;\n\n}\n\n2.3 UserModel\n------------------------------------------------\n<strong>&lt;?php\n\nnamespace </strong>App\\Models;\n\n<strong>use </strong>DB;\n<strong>use </strong>Illuminate\\Notifications\\Notifiable;\n<strong>use </strong>Illuminate\\Foundation\\Auth\\User <strong>as </strong>Authenticatable;\n<strong>use </strong>Spatie\\Permission\\Traits\\HasRoles;\n<strong>use </strong>Tymon\\JWTAuth\\Contracts\\JWTSubject;\n\n<strong>class </strong>User <strong>extends </strong><em>Authenticatable </em><strong>implements </strong>JWTSubject\n{\n    <strong>use </strong>Notifiable;\n    <strong>use </strong>HasRoles;\n\n\n    <strong>protected $table </strong>= <strong>\'users\'</strong>;\n\n    <em>/**\n     * The attributes that are mass assignable.\n     *\n     * </em><strong><em>@var </em></strong><em>array\n     */\n    </em><strong>protected $fillable </strong>= [\n        <strong>\'username\'</strong>,<strong>\'name\'</strong>, <strong>\'email\'</strong>, <strong>\'password\'</strong>,<strong>\'phone\'</strong>,<strong>\'uuid\'\n    </strong>];\n\n    <em>/**\n     * The attributes that should be hidden for arrays.\n     *\n     * </em><strong><em>@var </em></strong><em>array\n     */\n    </em><strong>protected $hidden </strong>= [\n        <strong>\'password\'</strong>, <strong>\'remember_token\'</strong>,\n    ];\n\n\n\n    <em>/**\n     * Get the identifier that will be stored in the subject claim of the JWT.\n     *\n     * </em><strong><em>@return </em></strong><em>mixed\n     */\n    </em><strong>public function </strong>getJWTIdentifier()\n    {\n        <strong>return </strong>$this->getKey();\n    }\n\n    <em>/**\n     * Return a key value array, containing any custom claims to be added to the JWT.\n     *\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public function </strong>getJWTCustomClaims()\n    {\n        <strong>return </strong>[];\n    }\n\n    <em>// 关联角色(roles被占用)\n    </em><strong>public function </strong>roleList()\n    {\n        <strong>return </strong>$this->hasMany(<strong>\'App\\Models\\ModelHasRoles\'</strong>, <strong>\'model_id\'</strong>, <strong>\'id\'</strong>);\n    }\n\n    <em>// 关联微信用户\n    </em><strong>public function </strong>wxUserList()\n    {\n        <strong>return </strong>$this->hasMany(<strong>\'App\\Models\\WxSalesman\'</strong>, <strong>\'user_id\'</strong>, <strong>\'id\'</strong>)->with(<strong>\'wxUser\'</strong>);\n    }\n\n\n    <em>// 判断用户的路由权限\n    </em><strong>public static function </strong>checkRouterPermission($userId, $route)\n    {\n        <em>// 权限表未定义路由，允许\n        </em><strong>if </strong>(Permission::<em>where</em>(<strong>\'route\'</strong>, $route)->doesntExist()){\n            <strong>return true</strong>;\n        }\n        <em>// 查询所属角色是否拥有路由权限\n        </em>$hasRolePermission = DB::<strong>table</strong>(<strong>\'model_has_roles AS ur\'</strong>)\n            ->join(<strong>\'role_has_permissions AS rp\'</strong>,  <strong>\'rp.role_id\'</strong>, <strong>\'=\'</strong>, <strong>\'ur.role_id\'</strong>)\n            ->join(<strong>\'permissions AS p\'</strong>,  <strong>\'p.id\'</strong>, <strong>\'=\'</strong>, <strong>\'rp.permission_id\'</strong>)\n            ->where([\n                <strong>\'ur.model_id\' </strong>=> $userId,\n                <strong>\'p.route\' </strong>=> $route\n            ])\n            ->exists();\n        <strong>if </strong>($hasRolePermission){\n            <strong>return true</strong>;\n        }\n        <em>// 查询用户是否直接拥有路由权限\n        </em>$hasUserPermission = DB::<strong>table</strong>(<strong>\'model_has_permissions AS up\'</strong>)\n            ->join(<strong>\'permissions AS p\'</strong>,  <strong>\'p.id\'</strong>, <strong>\'=\'</strong>, <strong>\'up.permission_id\'</strong>)\n            ->where([\n                <strong>\'up.model_id\' </strong>=> $userId,\n                <strong>\'p.route\' </strong>=> $route\n            ])\n            ->exists();\n        <strong>return </strong>$hasRolePermission || $hasUserPermission;\n    }\n}\n</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>3. 控制器逻辑</h4>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">3.1 OrganizationController\n------------------------------------------------\n<strong>&lt;?php\n</strong><em>/**\n * Created by PhpStorm.\n * User: WEN\n * Date: 2019/3/14\n * Time: 16:52\n */\n\n</em><strong>namespace </strong>App\\Http\\Controllers\\Admin;\n\n\n<strong>use </strong>App\\Http\\Controllers\\Controller;\n<strong>use </strong>App\\Models\\OrganizationModel;\n<strong>use </strong>App\\Models\\OrgUserModel;\n<strong>use </strong>App\\Models\\User;\n<strong>use </strong>App\\Service\\OrganizationService;\n<strong>use </strong>Illuminate\\Support\\Facades\\Request;\n<strong>use </strong>Illuminate\\Support\\Facades\\Validator;\n\n<strong>class </strong>OrganizationController <strong>extends </strong>Controller\n{\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>返回所有组织架构\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>index(){\n        <em>// 1. 获取所有组织\n        </em>$list = OrganizationModel::<em>get</em>([<strong>\'id\'</strong>,<strong>\'parent_id\'</strong>,<strong>\'level\'</strong>,<strong>\'name\'</strong>,<strong>\'en_name\'</strong>,<strong>\'icon_style\'</strong>,<strong>\'description\'</strong>,<strong>\'path\'</strong>])->toArray();\n\n        <em>// 2. 处理返回树\n        </em><strong>return </strong>$this->toSuccess(OrganizationService::<em>tree</em>($list));\n\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>新建组织部门\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>add()\n    {\n        <em>// 1. 获取请求数据 并校验\n        </em>$req = $this->getJsonContent();\n        $rules = [\n            <strong>\'parent_id\'  </strong>=> <strong>\'required|integer\'</strong>,\n            <strong>\'name\'  </strong>=> <strong>\'required\'</strong>,\n            <strong>\'en_name\'  </strong>=> <strong>\'required\'</strong>,\n            <strong>\'level\'  </strong>=> <strong>\'required|integer\'</strong>,\n            <strong>\'description\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'icon_style\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'path\'  </strong>=> <strong>\'required|string\'\n        </strong>];\n        $validator = Validator::<em>make</em>($req,$rules);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 创建数据入库\n        </em><strong>try</strong>{\n            $res = OrganizationModel::<em>create</em>($req);\n        }<strong>catch </strong>(\\PDOException $e){\n            <strong>return </strong>$this->toError($e->getMessage());\n        }\n\n\n        <strong>return </strong>$this->toSuccess($res);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>删除组织\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse|mixed\n     * </em><strong><em>@throws </em></strong><em>\\Exception\n     */\n    </em><strong>public function </strong>del()\n    {\n        <em>// 1. 获取请求内容 并校验\n        </em>$req = $this->getJsonContent();\n        $id = $req[<strong>\'id\'</strong>];\n        $validator = Validator::<em>make</em>($req,[<strong>\'id\'</strong>=><strong>\'required|integer\'</strong>]);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 检测是否存在子组织\n        </em><strong>if </strong>(OrganizationModel::<em>where</em>(<strong>\'parent_id\'</strong>,$id)->first()){\n            <strong>return </strong>$this->toError(<strong>\'存在子组织禁止删除\'</strong>);\n        }\n\n        <em>// 3. 执行删除\n        </em>\\DB::<strong>beginTransaction</strong>();\n        <strong>try</strong>{\n            <em>// 1. 删除组织\n            </em>OrganizationModel::<em>destroy</em>($id);\n\n            <em>// 2. 删除组织下的用户关系\n            </em>OrgUserModel::<em>where</em>(<strong>\'org_id\'</strong>,$id)->delete();\n\n            \\DB::<strong>commit</strong>();\n        }<strong>catch </strong>(\\PDOException $e){\n            \\DB::<strong>rollBack</strong>();\n            <strong>return </strong>$this->toError(<strong>\'删除组织失败\'</strong>);\n        }\n\n        <strong>return </strong>$this->toSuccess(<strong>\'删除组织成功\'</strong>);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>更新组织\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>edit()\n    {\n        <em>// 1. 获取前端请求数据  并校验\n        </em>$req = Request::<em>only</em>([<strong>\'id\'</strong>,<strong>\'name\'</strong>,<strong>\'en_name\'</strong>,<strong>\'icon_style\'</strong>,<strong>\'description\'</strong>,<strong>\'level\'</strong>]);\n        $rules = [\n            <strong>\'id\' </strong>=> <strong>\'required|integer\'</strong>,\n            <strong>\'name\'</strong>=><strong>\'required|string\'</strong>,\n            <strong>\'en_name\'  </strong>=> <strong>\'required|string\'</strong>,\n            <strong>\'icon_style\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'description\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'level\'  </strong>=> <strong>\'integer\'</strong>,\n            <strong>\'path\'  </strong>=> <strong>\'string\'</strong>,\n        ];\n        $validator = Validator::<em>make</em>($req,$rules);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 获取模型\n        </em>$row = OrganizationModel::<em>findOrFail</em>($req[<strong>\'id\'</strong>]);\n\n        <em>// 3. 执行更新\n        </em>$bol = $row->update($req);\n        <strong>if </strong>(<strong>false </strong>== $bol)\n            <strong>return </strong>$this->toError(<strong>\'更新组织失败\'</strong>);\n\n        <strong>return </strong>$this->toSuccess(<strong>\'更新组织成功\'</strong>);\n    }\n\n\n}\n\n3.2 OrganizationHasUsersController\n----------------------------------------------\n<strong>&lt;?php\n</strong><em>/**\n * Created by PhpStorm.\n * User: WEN\n * Date: 2019/3/14\n * Time: 16:52\n */\n\n</em><strong>namespace </strong>App\\Http\\Controllers\\Admin;\n\n\n<strong>use </strong>App\\Http\\Controllers\\Controller;\n<strong>use </strong>App\\Models\\OrganizationModel;\n<strong>use </strong>Illuminate\\Http\\Request;\n<strong>use </strong>Illuminate\\Support\\Facades\\Validator;\n\n<strong>class </strong>OrganizationHasUsersController <strong>extends </strong>Controller\n{\n    <strong>protected $req_fields </strong>= [\n        <strong>\'org_id\'  </strong>=> <strong>\'required|integer\'</strong>,\n        <strong>\'user_ids\'  </strong>=> <strong>\'array\'\n    </strong>];\n\n    <strong>protected $msgs </strong>= [\n        <strong>\'org_id.required\'</strong>=><strong>\'组织标识必传\'</strong>,\n        <strong>\'org_id.integer\'</strong>=><strong>\'组织标识必须是整数\'</strong>,\n        <strong>\'org_id.array\'</strong>=><strong>\'用户标识必须为数组\'</strong>,\n    ];\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>返回组织和用户的关系列表\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>index(Request $request){\n        <em>// 1. 获取前端请求数据 并校验\n        </em>$req = $request->only(<strong>\'id\'</strong>);\n        $validator = Validator::<em>make</em>($req, [<strong>\'id\'  </strong>=> <strong>\'required|integer\'</strong>],[<strong>\'id.required\'</strong>=><strong>\'组织标识必传\'</strong>,<strong>\'id.integer\'</strong>=><strong>\'组织标识必须是整数\'</strong>]);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 获取组织和用户关系\n        </em>$users = OrganizationModel::<em>getOrgHaveUsers</em>($req[<strong>\'id\'</strong>]);\n\n        <em>// 4. 响应前端\n        </em><strong>return </strong>$this->toSuccess($users);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>组织绑定用户\n     * </em><strong><em>@param </em></strong><em>Request $request\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse|mixed\n     */\n    </em><strong>public function </strong>bindUsers(Request $request)\n    {\n        <em>// 1. 获取请求数据 并校验\n        </em>$req = $request->only(<em>array_keys</em>($this-><strong>req_fields</strong>));\n        $validator = Validator::<em>make</em>($req,$this-><strong>req_fields</strong>,$this-><strong>msgs</strong>);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 获取当前组织下原绑定的用户\n        </em><strong>try</strong>{\n            $res = OrganizationModel::<em>bindUsers</em>($req[<strong>\'org_id\'</strong>],$req[<strong>\'user_ids\'</strong>]);\n        }<strong>catch </strong>(\\PDOException $e){\n            <strong>return </strong>$this->toError($e->getMessage());\n        }\n\n\n        <strong>return </strong>$this->toSuccess($res);\n    }\n\n}</pre>\n<!-- /wp:preformatted -->','PHP+MYSQL无限极分类','','publish','open','open','','phpmysql%e6%97%a0%e9%99%90%e6%9e%81%e5%88%86%e7%b1%bb','','','2019-07-23 17:30:04','2019-07-23 09:30:04','',0,'http://long521.cn/?p=233',0,'post','',0),(234,1,'2019-07-23 12:29:58','2019-07-23 04:29:58','','PHP+MYSQL无限极分类','','inherit','closed','closed','','233-revision-v1','','','2019-07-23 12:29:58','2019-07-23 04:29:58','',233,'http://long521.cn/2019/07/23/233-revision-v1/',0,'revision','',0),(236,1,'2019-07-23 17:29:19','2019-07-23 09:29:19','<!-- wp:list {\"ordered\":true} -->\n<ol><li>mysql表设计</li><li>索引设计</li><li>模型查询</li><li>控制器调用</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>1. mysql表设计</h4>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">1.1  组织表 （分类表）\n-------------------------------------------------\n CREATE TABLE <code>organizations</code> (\n   <code>id</code> int(10) unsigned NOT NULL AUTO_INCREMENT,\n   <code>parent_id</code> int(11) unsigned NOT NULL DEFAULT \'0\',\n   <code>name</code> varchar(100) NOT NULL,\n   <code>en_name</code> varchar(100) NOT NULL DEFAULT \'\',\n   <code>description</code> varchar(100) NOT NULL DEFAULT \'\',\n   <code>icon_style</code> varchar(191) NOT NULL DEFAULT \'\' COMMENT \'图标style\',\n   <code>level</code> tinyint(2) unsigned NOT NULL DEFAULT \'0\' COMMENT \'级别\',\n   <code>path</code> varchar(255) NOT NULL COMMENT \'组织路径用于快速查询\',\n   <code>created_at</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   <code>updated_at</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   PRIMARY KEY (<code>id</code>),\n   UNIQUE KEY <code>name</code> (<code>name</code>) USING BTREE,\n   KEY <code>path</code> (<code>path</code>)\n ) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 \n\n1.2  用户表 \n----------------------------------------------------\nCREATE TABLE <code>users</code> (\n   <code>id</code> int(10) unsigned NOT NULL AUTO_INCREMENT,\n   <code>username</code> varchar(191) NOT NULL,\n   <code>phone</code> varchar(191) NOT NULL,\n   <code>name</code> varchar(191) NOT NULL,\n   <code>email</code> varchar(191) NOT NULL,\n   <code>password</code> varchar(191) NOT NULL,\n   <code>created_at</code> timestamp NULL DEFAULT NULL,\n   <code>updated_at</code> timestamp NULL DEFAULT NULL,\n   PRIMARY KEY (<code>id</code>),\n   UNIQUE KEY <code>users_username_unique</code> (<code>username</code>)\n ) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4\n\n1.3  用户组织关系表 （中间表）\n-------------------------------------------------------\nCREATE TABLE <code>org_user</code> (\n   <code>user_id</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   <code>org_id</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   UNIQUE KEY <code>user-org</code> (<code>user_id</code>,<code>org_id</code>) USING BTREE\n ) ENGINE=MyISAM DEFAULT CHARSET=utf8</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>2. laravel模型构建</h4>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">2.1  OrganizationModel\n----------------------------------------\n<strong>&lt;?php\n\nnamespace </strong>App\\Models;\n\n<strong>class </strong>OrganizationModel <strong>extends </strong>BaseModel\n{\n    <em>// 与模型关联的数据表 string\n    </em><strong>protected $table </strong>= <strong>\'organizations\'</strong>;\n\n    <em>// 模型的日期字段的存储格式 参考date()函数参一说明 string\n    </em><strong>protected $dateFormat </strong>= <strong>\'U\'</strong>; <em>// U 秒时间戳\n\n    // 批量赋值黑名单\n    </em><strong>protected $guarded </strong>= [<strong>\'id\'</strong>, <strong>\'created_at\'</strong>, <strong>\'updated_at\'</strong>];\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>处理组织分类树\n     * </em><strong><em>@param </em></strong><em>array $list\n     * </em><strong><em>@param </em></strong><em>string $pk\n     * </em><strong><em>@param </em></strong><em>string $pid\n     * </em><strong><em>@param </em></strong><em>string $child\n     * </em><strong><em>@param </em></strong><em>int $root\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public static function </strong>tree($list=[], $pk=<strong>\'id\'</strong>, $pid = <strong>\'parent_id\'</strong>, $child = <strong>\'_child\'</strong>, $root = 0)\n    {\n        <em>// 创建Tree\n        </em>$tree = <strong>array</strong>();\n        <strong>if</strong>(<em>is_array</em>($list) &amp;&amp; $list) {\n            <em>// 创建基于主键的数组引用\n            </em>$refer = <strong>array</strong>();\n            <strong>foreach </strong>($list <strong>as </strong>$key => $data) {\n                $refer[$data[$pk]] =&amp; $list[$key];\n            }\n            <strong>foreach </strong>($list <strong>as </strong>$key => $data) {\n                <em>// 判断是否存在parent\n                </em>$parentId =  $data[$pid];\n                <strong>if </strong>($root == $parentId) {\n                    $tree[] =&amp; $list[$key];\n                }<strong>else</strong>{\n                    <strong>if </strong>(<strong>isset</strong>($refer[$parentId])) {\n                        $parent =&amp; $refer[$parentId];\n                        $parent[$child][] =&amp; $list[$key];\n                    }\n                }\n            }\n        }\n        <strong>return </strong>$tree;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>获取所有子级组织\n     * </em><strong><em>@param </em></strong><em>int $org || organization model\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public static function </strong>getAllChild($org=1){\n        <strong>if</strong>(<em>is_int</em>($org))\n            $org = <strong>self</strong>::<em>findOrFail</em>($org);\n\n        <strong>return self</strong>::<em>where</em>(<strong>\'path\'</strong>,<strong>\'like\'</strong>,$org-><strong>path </strong>. $org-><strong>id </strong>. <strong>\'-%\'</strong>)->get();\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>获取所有子级组织id\n     * </em><strong><em>@param </em></strong><em>int $org || organization model\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public static function </strong>getAllChildId($org=1){\n        <strong>return self</strong>::<em>getAllChild</em>($org)->pluck(<strong>\'id\'</strong>);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>获取组织和用户关系\n     * </em><strong><em>@param </em></strong><em>int $org || organization model\n     * </em><strong><em>@return </em></strong><em>User[]|\\Illuminate\\Database\\Eloquent\\Collection\n     */\n    </em><strong>public static function </strong>getOrgHaveUsers($org=1){\n        <em>// 1. 获取所有子组织id和本身id\n        </em>$self_id = <em>is_int</em>($org)? $org : $org-><strong>id</strong>;\n        $org_ids = <strong>self</strong>::<em>getAllChildId</em>($org)->push($self_id)->toArray();\n        $org_users = OrgUserModel::<em>whereIn</em>(<strong>\'org_id\'</strong>,$org_ids)->pluck(<strong>\'user_id\'</strong>)->unique();\n\n        <em>// 2. 获取所有用户\n        </em>$users = User::<em>all</em>([<strong>\'id\'</strong>,<strong>\'username\'</strong>,<strong>\'name\'</strong>]);\n\n        <em>// 3. 处理组织和用户绑定关系\n        </em>$users->transform(<strong>function </strong>($item)<strong>use</strong>($org_users){\n            $item[<strong>\'checked\'</strong>] = ( $org_users->search($item[<strong>\'id\'</strong>]) === <strong>false </strong>)?<strong>false</strong>:<strong>true</strong>;\n            <strong>return </strong>$item;\n        });\n\n        <strong>return </strong>$users;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>通过组织获取所属用户模型\n     * </em><strong><em>@param </em></strong><em>int $org || Organization model\n     * </em><strong><em>@return </em></strong><em>mixed\n     */\n    </em><strong>public static function </strong>getAllUsersByOrg($org=1){\n        <em>// 1. 获取所有属于该组织树下的user_id\n        </em>$user_ids = <strong>self</strong>::<em>getAllUserIdsByOrg</em>($org);\n\n        <em>// 2. 获取所有属于该组织下的用户模型\n        </em>$users = User::<em>whereIn</em>(<strong>\'id\'</strong>,$user_ids)->get();\n\n        <strong>return </strong>$users;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>通过组织获取所属用户id数组\n     * </em><strong><em>@param </em></strong><em>int $org || Organization model\n     * </em><strong><em>@return </em></strong><em>array $user_ids\n     */\n    </em><strong>public static function </strong>getAllUserIdsByOrg($org=1){\n        <em>// 1. 获取所有子组织id和本身id\n        </em>$self_id = <em>is_int</em>($org)? $org : $org-><strong>id</strong>;\n        $org_ids = <strong>self</strong>::<em>getAllChildId</em>($org)->push($self_id)->toArray();\n\n        <em>// 2. 获取所有属于该组织树下的user_id\n        </em>$user_ids = OrgUserModel::<em>whereIn</em>(<strong>\'org_id\'</strong>,$org_ids)->pluck(<strong>\'user_id\'</strong>)->unique()->toArray();\n\n        <strong>return </strong>$user_ids;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>给组织绑定用户\n     * </em><strong><em>@param </em></strong><em>$org_id\n     * </em><strong><em>@param </em></strong><em>array $users\n     * </em><strong><em>@return </em></strong><em>bool|mixed\n     */\n    </em><strong>public static function </strong>bindUsers($org_id,$users=[]){\n        <em>// 1. 用户为空直接删除并返回\n        </em><strong>if</strong>(!$users){\n            $res = OrgUserModel::<em>where</em>(<strong>\'org_id\'</strong>,$org_id)->delete();\n            <strong>return </strong>$res;\n        }\n\n        <em>// 2. 获取该组织树下原绑定用户\n        </em>$origin_bind_users = <strong>self</strong>::<em>getAllUserIdsByOrg</em>($org_id);\n\n        <em>// 3. 处理需要解绑与绑定的用户关系\n        </em>$intersect = <em>array_intersect</em>($origin_bind_users,$users);\n        $need_delete = <em>array_diff</em>($origin_bind_users,$intersect);\n        $need_add = <em>array_diff</em>($users,$intersect);\n        $insert_data = [];\n        <strong>foreach </strong>($need_add <strong>as </strong>$val){\n            $insert_data[] = [<strong>\'org_id\'</strong>=>$org_id,<strong>\'user_id\'</strong>=>$val];\n        }\n\n        <em>// 4. 执行操作\n        </em>$res = OrgUserModel::<em>where</em>(<strong>\'org_id\'</strong>,$org_id)->whereIn(<strong>\'user_id\'</strong>,$need_delete)->delete();\n        <strong>if</strong>($insert_data) $res = OrgUserModel::<em>insert</em>($insert_data);\n\n        <strong>return </strong>$res;\n    }\n\n\n}\n\n2.2  OrgUserModel\n----------------------------------------\n<strong>&lt;?php\n\nnamespace </strong>App\\Models;\n\n<strong>class </strong>OrgUserModel <strong>extends </strong>BaseModel\n{\n    <em>// 与模型关联的数据表 string\n    </em><strong>protected $table </strong>= <strong>\'org_user\'</strong>;\n\n    <em>// 该模型是否被自动维护时间戳 bool\n    </em><strong>public $timestamps </strong>= <strong>false</strong>;\n\n}\n\n2.3 UserModel\n------------------------------------------------\n<strong>&lt;?php\n\nnamespace </strong>App\\Models;\n\n<strong>use </strong>DB;\n<strong>use </strong>Illuminate\\Notifications\\Notifiable;\n<strong>use </strong>Illuminate\\Foundation\\Auth\\User <strong>as </strong>Authenticatable;\n<strong>use </strong>Spatie\\Permission\\Traits\\HasRoles;\n<strong>use </strong>Tymon\\JWTAuth\\Contracts\\JWTSubject;\n\n<strong>class </strong>User <strong>extends </strong><em>Authenticatable </em><strong>implements </strong>JWTSubject\n{\n    <strong>use </strong>Notifiable;\n    <strong>use </strong>HasRoles;\n\n\n    <strong>protected $table </strong>= <strong>\'users\'</strong>;\n\n    <em>/**\n     * The attributes that are mass assignable.\n     *\n     * </em><strong><em>@var </em></strong><em>array\n     */\n    </em><strong>protected $fillable </strong>= [\n        <strong>\'username\'</strong>,<strong>\'name\'</strong>, <strong>\'email\'</strong>, <strong>\'password\'</strong>,<strong>\'phone\'</strong>,<strong>\'uuid\'\n    </strong>];\n\n    <em>/**\n     * The attributes that should be hidden for arrays.\n     *\n     * </em><strong><em>@var </em></strong><em>array\n     */\n    </em><strong>protected $hidden </strong>= [\n        <strong>\'password\'</strong>, <strong>\'remember_token\'</strong>,\n    ];\n\n\n\n    <em>/**\n     * Get the identifier that will be stored in the subject claim of the JWT.\n     *\n     * </em><strong><em>@return </em></strong><em>mixed\n     */\n    </em><strong>public function </strong>getJWTIdentifier()\n    {\n        <strong>return </strong>$this->getKey();\n    }\n\n    <em>/**\n     * Return a key value array, containing any custom claims to be added to the JWT.\n     *\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public function </strong>getJWTCustomClaims()\n    {\n        <strong>return </strong>[];\n    }\n\n    <em>// 关联角色(roles被占用)\n    </em><strong>public function </strong>roleList()\n    {\n        <strong>return </strong>$this->hasMany(<strong>\'App\\Models\\ModelHasRoles\'</strong>, <strong>\'model_id\'</strong>, <strong>\'id\'</strong>);\n    }\n\n    <em>// 关联微信用户\n    </em><strong>public function </strong>wxUserList()\n    {\n        <strong>return </strong>$this->hasMany(<strong>\'App\\Models\\WxSalesman\'</strong>, <strong>\'user_id\'</strong>, <strong>\'id\'</strong>)->with(<strong>\'wxUser\'</strong>);\n    }\n\n\n    <em>// 判断用户的路由权限\n    </em><strong>public static function </strong>checkRouterPermission($userId, $route)\n    {\n        <em>// 权限表未定义路由，允许\n        </em><strong>if </strong>(Permission::<em>where</em>(<strong>\'route\'</strong>, $route)->doesntExist()){\n            <strong>return true</strong>;\n        }\n        <em>// 查询所属角色是否拥有路由权限\n        </em>$hasRolePermission = DB::<strong>table</strong>(<strong>\'model_has_roles AS ur\'</strong>)\n            ->join(<strong>\'role_has_permissions AS rp\'</strong>,  <strong>\'rp.role_id\'</strong>, <strong>\'=\'</strong>, <strong>\'ur.role_id\'</strong>)\n            ->join(<strong>\'permissions AS p\'</strong>,  <strong>\'p.id\'</strong>, <strong>\'=\'</strong>, <strong>\'rp.permission_id\'</strong>)\n            ->where([\n                <strong>\'ur.model_id\' </strong>=> $userId,\n                <strong>\'p.route\' </strong>=> $route\n            ])\n            ->exists();\n        <strong>if </strong>($hasRolePermission){\n            <strong>return true</strong>;\n        }\n        <em>// 查询用户是否直接拥有路由权限\n        </em>$hasUserPermission = DB::<strong>table</strong>(<strong>\'model_has_permissions AS up\'</strong>)\n            ->join(<strong>\'permissions AS p\'</strong>,  <strong>\'p.id\'</strong>, <strong>\'=\'</strong>, <strong>\'up.permission_id\'</strong>)\n            ->where([\n                <strong>\'up.model_id\' </strong>=> $userId,\n                <strong>\'p.route\' </strong>=> $route\n            ])\n            ->exists();\n        <strong>return </strong>$hasRolePermission || $hasUserPermission;\n    }\n}\n</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>3. 控制器逻辑</h4>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">3.1 OrganizationController\n------------------------------------------------\n<strong>&lt;?php\n</strong><em>/**\n * Created by PhpStorm.\n * User: WEN\n * Date: 2019/3/14\n * Time: 16:52\n */\n\n</em><strong>namespace </strong>App\\Http\\Controllers\\Admin;\n\n\n<strong>use </strong>App\\Http\\Controllers\\Controller;\n<strong>use </strong>App\\Models\\OrganizationModel;\n<strong>use </strong>App\\Models\\OrgUserModel;\n<strong>use </strong>App\\Models\\User;\n<strong>use </strong>App\\Service\\OrganizationService;\n<strong>use </strong>Illuminate\\Support\\Facades\\Request;\n<strong>use </strong>Illuminate\\Support\\Facades\\Validator;\n\n<strong>class </strong>OrganizationController <strong>extends </strong>Controller\n{\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>返回所有组织架构\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>index(){\n        <em>// 1. 获取所有组织\n        </em>$list = OrganizationModel::<em>get</em>([<strong>\'id\'</strong>,<strong>\'parent_id\'</strong>,<strong>\'level\'</strong>,<strong>\'name\'</strong>,<strong>\'en_name\'</strong>,<strong>\'icon_style\'</strong>,<strong>\'description\'</strong>,<strong>\'path\'</strong>])->toArray();\n\n        <em>// 2. 处理返回树\n        </em><strong>return </strong>$this->toSuccess(OrganizationService::<em>tree</em>($list));\n\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>新建组织部门\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>add()\n    {\n        <em>// 1. 获取请求数据 并校验\n        </em>$req = $this->getJsonContent();\n        $rules = [\n            <strong>\'parent_id\'  </strong>=> <strong>\'required|integer\'</strong>,\n            <strong>\'name\'  </strong>=> <strong>\'required\'</strong>,\n            <strong>\'en_name\'  </strong>=> <strong>\'required\'</strong>,\n            <strong>\'level\'  </strong>=> <strong>\'required|integer\'</strong>,\n            <strong>\'description\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'icon_style\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'path\'  </strong>=> <strong>\'required|string\'\n        </strong>];\n        $validator = Validator::<em>make</em>($req,$rules);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 创建数据入库\n        </em><strong>try</strong>{\n            $res = OrganizationModel::<em>create</em>($req);\n        }<strong>catch </strong>(\\PDOException $e){\n            <strong>return </strong>$this->toError($e->getMessage());\n        }\n\n\n        <strong>return </strong>$this->toSuccess($res);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>删除组织\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse|mixed\n     * </em><strong><em>@throws </em></strong><em>\\Exception\n     */\n    </em><strong>public function </strong>del()\n    {\n        <em>// 1. 获取请求内容 并校验\n        </em>$req = $this->getJsonContent();\n        $id = $req[<strong>\'id\'</strong>];\n        $validator = Validator::<em>make</em>($req,[<strong>\'id\'</strong>=><strong>\'required|integer\'</strong>]);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 检测是否存在子组织\n        </em><strong>if </strong>(OrganizationModel::<em>where</em>(<strong>\'parent_id\'</strong>,$id)->first()){\n            <strong>return </strong>$this->toError(<strong>\'存在子组织禁止删除\'</strong>);\n        }\n\n        <em>// 3. 执行删除\n        </em>\\DB::<strong>beginTransaction</strong>();\n        <strong>try</strong>{\n            <em>// 1. 删除组织\n            </em>OrganizationModel::<em>destroy</em>($id);\n\n            <em>// 2. 删除组织下的用户关系\n            </em>OrgUserModel::<em>where</em>(<strong>\'org_id\'</strong>,$id)->delete();\n\n            \\DB::<strong>commit</strong>();\n        }<strong>catch </strong>(\\PDOException $e){\n            \\DB::<strong>rollBack</strong>();\n            <strong>return </strong>$this->toError(<strong>\'删除组织失败\'</strong>);\n        }\n\n        <strong>return </strong>$this->toSuccess(<strong>\'删除组织成功\'</strong>);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>更新组织\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>edit()\n    {\n        <em>// 1. 获取前端请求数据  并校验\n        </em>$req = Request::<em>only</em>([<strong>\'id\'</strong>,<strong>\'name\'</strong>,<strong>\'en_name\'</strong>,<strong>\'icon_style\'</strong>,<strong>\'description\'</strong>,<strong>\'level\'</strong>]);\n        $rules = [\n            <strong>\'id\' </strong>=> <strong>\'required|integer\'</strong>,\n            <strong>\'name\'</strong>=><strong>\'required|string\'</strong>,\n            <strong>\'en_name\'  </strong>=> <strong>\'required|string\'</strong>,\n            <strong>\'icon_style\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'description\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'level\'  </strong>=> <strong>\'integer\'</strong>,\n            <strong>\'path\'  </strong>=> <strong>\'string\'</strong>,\n        ];\n        $validator = Validator::<em>make</em>($req,$rules);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 获取模型\n        </em>$row = OrganizationModel::<em>findOrFail</em>($req[<strong>\'id\'</strong>]);\n\n        <em>// 3. 执行更新\n        </em>$bol = $row->update($req);\n        <strong>if </strong>(<strong>false </strong>== $bol)\n            <strong>return </strong>$this->toError(<strong>\'更新组织失败\'</strong>);\n\n        <strong>return </strong>$this->toSuccess(<strong>\'更新组织成功\'</strong>);\n    }\n\n\n}\n\n3.2 OrganizationHasUsersController\n----------------------------------------------\n</pre>\n<!-- /wp:preformatted -->','PHP+MYSQL无限极分类','','inherit','closed','closed','','233-autosave-v1','','','2019-07-23 17:29:19','2019-07-23 09:29:19','',233,'http://long521.cn/2019/07/23/233-autosave-v1/',0,'revision','',0),(237,1,'2019-07-23 17:30:04','2019-07-23 09:30:04','<!-- wp:list {\"ordered\":true} -->\n<ol><li>mysql表设计</li><li>索引设计</li><li>模型查询</li><li>控制器调用</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>1. mysql表设计</h4>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">1.1  组织表 （分类表）\n-------------------------------------------------\n CREATE TABLE <code>organizations</code> (\n   <code>id</code> int(10) unsigned NOT NULL AUTO_INCREMENT,\n   <code>parent_id</code> int(11) unsigned NOT NULL DEFAULT \'0\',\n   <code>name</code> varchar(100) NOT NULL,\n   <code>en_name</code> varchar(100) NOT NULL DEFAULT \'\',\n   <code>description</code> varchar(100) NOT NULL DEFAULT \'\',\n   <code>icon_style</code> varchar(191) NOT NULL DEFAULT \'\' COMMENT \'图标style\',\n   <code>level</code> tinyint(2) unsigned NOT NULL DEFAULT \'0\' COMMENT \'级别\',\n   <code>path</code> varchar(255) NOT NULL COMMENT \'组织路径用于快速查询\',\n   <code>created_at</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   <code>updated_at</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   PRIMARY KEY (<code>id</code>),\n   UNIQUE KEY <code>name</code> (<code>name</code>) USING BTREE,\n   KEY <code>path</code> (<code>path</code>)\n ) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 \n\n1.2  用户表 \n----------------------------------------------------\nCREATE TABLE <code>users</code> (\n   <code>id</code> int(10) unsigned NOT NULL AUTO_INCREMENT,\n   <code>username</code> varchar(191) NOT NULL,\n   <code>phone</code> varchar(191) NOT NULL,\n   <code>name</code> varchar(191) NOT NULL,\n   <code>email</code> varchar(191) NOT NULL,\n   <code>password</code> varchar(191) NOT NULL,\n   <code>created_at</code> timestamp NULL DEFAULT NULL,\n   <code>updated_at</code> timestamp NULL DEFAULT NULL,\n   PRIMARY KEY (<code>id</code>),\n   UNIQUE KEY <code>users_username_unique</code> (<code>username</code>)\n ) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4\n\n1.3  用户组织关系表 （中间表）\n-------------------------------------------------------\nCREATE TABLE <code>org_user</code> (\n   <code>user_id</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   <code>org_id</code> int(10) unsigned NOT NULL DEFAULT \'0\',\n   UNIQUE KEY <code>user-org</code> (<code>user_id</code>,<code>org_id</code>) USING BTREE\n ) ENGINE=MyISAM DEFAULT CHARSET=utf8</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>2. laravel模型构建</h4>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">2.1  OrganizationModel\n----------------------------------------\n<strong>&lt;?php\n\nnamespace </strong>App\\Models;\n\n<strong>class </strong>OrganizationModel <strong>extends </strong>BaseModel\n{\n    <em>// 与模型关联的数据表 string\n    </em><strong>protected $table </strong>= <strong>\'organizations\'</strong>;\n\n    <em>// 模型的日期字段的存储格式 参考date()函数参一说明 string\n    </em><strong>protected $dateFormat </strong>= <strong>\'U\'</strong>; <em>// U 秒时间戳\n\n    // 批量赋值黑名单\n    </em><strong>protected $guarded </strong>= [<strong>\'id\'</strong>, <strong>\'created_at\'</strong>, <strong>\'updated_at\'</strong>];\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>处理组织分类树\n     * </em><strong><em>@param </em></strong><em>array $list\n     * </em><strong><em>@param </em></strong><em>string $pk\n     * </em><strong><em>@param </em></strong><em>string $pid\n     * </em><strong><em>@param </em></strong><em>string $child\n     * </em><strong><em>@param </em></strong><em>int $root\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public static function </strong>tree($list=[], $pk=<strong>\'id\'</strong>, $pid = <strong>\'parent_id\'</strong>, $child = <strong>\'_child\'</strong>, $root = 0)\n    {\n        <em>// 创建Tree\n        </em>$tree = <strong>array</strong>();\n        <strong>if</strong>(<em>is_array</em>($list) &amp;&amp; $list) {\n            <em>// 创建基于主键的数组引用\n            </em>$refer = <strong>array</strong>();\n            <strong>foreach </strong>($list <strong>as </strong>$key => $data) {\n                $refer[$data[$pk]] =&amp; $list[$key];\n            }\n            <strong>foreach </strong>($list <strong>as </strong>$key => $data) {\n                <em>// 判断是否存在parent\n                </em>$parentId =  $data[$pid];\n                <strong>if </strong>($root == $parentId) {\n                    $tree[] =&amp; $list[$key];\n                }<strong>else</strong>{\n                    <strong>if </strong>(<strong>isset</strong>($refer[$parentId])) {\n                        $parent =&amp; $refer[$parentId];\n                        $parent[$child][] =&amp; $list[$key];\n                    }\n                }\n            }\n        }\n        <strong>return </strong>$tree;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>获取所有子级组织\n     * </em><strong><em>@param </em></strong><em>int $org || organization model\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public static function </strong>getAllChild($org=1){\n        <strong>if</strong>(<em>is_int</em>($org))\n            $org = <strong>self</strong>::<em>findOrFail</em>($org);\n\n        <strong>return self</strong>::<em>where</em>(<strong>\'path\'</strong>,<strong>\'like\'</strong>,$org-><strong>path </strong>. $org-><strong>id </strong>. <strong>\'-%\'</strong>)->get();\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>获取所有子级组织id\n     * </em><strong><em>@param </em></strong><em>int $org || organization model\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public static function </strong>getAllChildId($org=1){\n        <strong>return self</strong>::<em>getAllChild</em>($org)->pluck(<strong>\'id\'</strong>);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>获取组织和用户关系\n     * </em><strong><em>@param </em></strong><em>int $org || organization model\n     * </em><strong><em>@return </em></strong><em>User[]|\\Illuminate\\Database\\Eloquent\\Collection\n     */\n    </em><strong>public static function </strong>getOrgHaveUsers($org=1){\n        <em>// 1. 获取所有子组织id和本身id\n        </em>$self_id = <em>is_int</em>($org)? $org : $org-><strong>id</strong>;\n        $org_ids = <strong>self</strong>::<em>getAllChildId</em>($org)->push($self_id)->toArray();\n        $org_users = OrgUserModel::<em>whereIn</em>(<strong>\'org_id\'</strong>,$org_ids)->pluck(<strong>\'user_id\'</strong>)->unique();\n\n        <em>// 2. 获取所有用户\n        </em>$users = User::<em>all</em>([<strong>\'id\'</strong>,<strong>\'username\'</strong>,<strong>\'name\'</strong>]);\n\n        <em>// 3. 处理组织和用户绑定关系\n        </em>$users->transform(<strong>function </strong>($item)<strong>use</strong>($org_users){\n            $item[<strong>\'checked\'</strong>] = ( $org_users->search($item[<strong>\'id\'</strong>]) === <strong>false </strong>)?<strong>false</strong>:<strong>true</strong>;\n            <strong>return </strong>$item;\n        });\n\n        <strong>return </strong>$users;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>通过组织获取所属用户模型\n     * </em><strong><em>@param </em></strong><em>int $org || Organization model\n     * </em><strong><em>@return </em></strong><em>mixed\n     */\n    </em><strong>public static function </strong>getAllUsersByOrg($org=1){\n        <em>// 1. 获取所有属于该组织树下的user_id\n        </em>$user_ids = <strong>self</strong>::<em>getAllUserIdsByOrg</em>($org);\n\n        <em>// 2. 获取所有属于该组织下的用户模型\n        </em>$users = User::<em>whereIn</em>(<strong>\'id\'</strong>,$user_ids)->get();\n\n        <strong>return </strong>$users;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>通过组织获取所属用户id数组\n     * </em><strong><em>@param </em></strong><em>int $org || Organization model\n     * </em><strong><em>@return </em></strong><em>array $user_ids\n     */\n    </em><strong>public static function </strong>getAllUserIdsByOrg($org=1){\n        <em>// 1. 获取所有子组织id和本身id\n        </em>$self_id = <em>is_int</em>($org)? $org : $org-><strong>id</strong>;\n        $org_ids = <strong>self</strong>::<em>getAllChildId</em>($org)->push($self_id)->toArray();\n\n        <em>// 2. 获取所有属于该组织树下的user_id\n        </em>$user_ids = OrgUserModel::<em>whereIn</em>(<strong>\'org_id\'</strong>,$org_ids)->pluck(<strong>\'user_id\'</strong>)->unique()->toArray();\n\n        <strong>return </strong>$user_ids;\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>给组织绑定用户\n     * </em><strong><em>@param </em></strong><em>$org_id\n     * </em><strong><em>@param </em></strong><em>array $users\n     * </em><strong><em>@return </em></strong><em>bool|mixed\n     */\n    </em><strong>public static function </strong>bindUsers($org_id,$users=[]){\n        <em>// 1. 用户为空直接删除并返回\n        </em><strong>if</strong>(!$users){\n            $res = OrgUserModel::<em>where</em>(<strong>\'org_id\'</strong>,$org_id)->delete();\n            <strong>return </strong>$res;\n        }\n\n        <em>// 2. 获取该组织树下原绑定用户\n        </em>$origin_bind_users = <strong>self</strong>::<em>getAllUserIdsByOrg</em>($org_id);\n\n        <em>// 3. 处理需要解绑与绑定的用户关系\n        </em>$intersect = <em>array_intersect</em>($origin_bind_users,$users);\n        $need_delete = <em>array_diff</em>($origin_bind_users,$intersect);\n        $need_add = <em>array_diff</em>($users,$intersect);\n        $insert_data = [];\n        <strong>foreach </strong>($need_add <strong>as </strong>$val){\n            $insert_data[] = [<strong>\'org_id\'</strong>=>$org_id,<strong>\'user_id\'</strong>=>$val];\n        }\n\n        <em>// 4. 执行操作\n        </em>$res = OrgUserModel::<em>where</em>(<strong>\'org_id\'</strong>,$org_id)->whereIn(<strong>\'user_id\'</strong>,$need_delete)->delete();\n        <strong>if</strong>($insert_data) $res = OrgUserModel::<em>insert</em>($insert_data);\n\n        <strong>return </strong>$res;\n    }\n\n\n}\n\n2.2  OrgUserModel\n----------------------------------------\n<strong>&lt;?php\n\nnamespace </strong>App\\Models;\n\n<strong>class </strong>OrgUserModel <strong>extends </strong>BaseModel\n{\n    <em>// 与模型关联的数据表 string\n    </em><strong>protected $table </strong>= <strong>\'org_user\'</strong>;\n\n    <em>// 该模型是否被自动维护时间戳 bool\n    </em><strong>public $timestamps </strong>= <strong>false</strong>;\n\n}\n\n2.3 UserModel\n------------------------------------------------\n<strong>&lt;?php\n\nnamespace </strong>App\\Models;\n\n<strong>use </strong>DB;\n<strong>use </strong>Illuminate\\Notifications\\Notifiable;\n<strong>use </strong>Illuminate\\Foundation\\Auth\\User <strong>as </strong>Authenticatable;\n<strong>use </strong>Spatie\\Permission\\Traits\\HasRoles;\n<strong>use </strong>Tymon\\JWTAuth\\Contracts\\JWTSubject;\n\n<strong>class </strong>User <strong>extends </strong><em>Authenticatable </em><strong>implements </strong>JWTSubject\n{\n    <strong>use </strong>Notifiable;\n    <strong>use </strong>HasRoles;\n\n\n    <strong>protected $table </strong>= <strong>\'users\'</strong>;\n\n    <em>/**\n     * The attributes that are mass assignable.\n     *\n     * </em><strong><em>@var </em></strong><em>array\n     */\n    </em><strong>protected $fillable </strong>= [\n        <strong>\'username\'</strong>,<strong>\'name\'</strong>, <strong>\'email\'</strong>, <strong>\'password\'</strong>,<strong>\'phone\'</strong>,<strong>\'uuid\'\n    </strong>];\n\n    <em>/**\n     * The attributes that should be hidden for arrays.\n     *\n     * </em><strong><em>@var </em></strong><em>array\n     */\n    </em><strong>protected $hidden </strong>= [\n        <strong>\'password\'</strong>, <strong>\'remember_token\'</strong>,\n    ];\n\n\n\n    <em>/**\n     * Get the identifier that will be stored in the subject claim of the JWT.\n     *\n     * </em><strong><em>@return </em></strong><em>mixed\n     */\n    </em><strong>public function </strong>getJWTIdentifier()\n    {\n        <strong>return </strong>$this->getKey();\n    }\n\n    <em>/**\n     * Return a key value array, containing any custom claims to be added to the JWT.\n     *\n     * </em><strong><em>@return </em></strong><em>array\n     */\n    </em><strong>public function </strong>getJWTCustomClaims()\n    {\n        <strong>return </strong>[];\n    }\n\n    <em>// 关联角色(roles被占用)\n    </em><strong>public function </strong>roleList()\n    {\n        <strong>return </strong>$this->hasMany(<strong>\'App\\Models\\ModelHasRoles\'</strong>, <strong>\'model_id\'</strong>, <strong>\'id\'</strong>);\n    }\n\n    <em>// 关联微信用户\n    </em><strong>public function </strong>wxUserList()\n    {\n        <strong>return </strong>$this->hasMany(<strong>\'App\\Models\\WxSalesman\'</strong>, <strong>\'user_id\'</strong>, <strong>\'id\'</strong>)->with(<strong>\'wxUser\'</strong>);\n    }\n\n\n    <em>// 判断用户的路由权限\n    </em><strong>public static function </strong>checkRouterPermission($userId, $route)\n    {\n        <em>// 权限表未定义路由，允许\n        </em><strong>if </strong>(Permission::<em>where</em>(<strong>\'route\'</strong>, $route)->doesntExist()){\n            <strong>return true</strong>;\n        }\n        <em>// 查询所属角色是否拥有路由权限\n        </em>$hasRolePermission = DB::<strong>table</strong>(<strong>\'model_has_roles AS ur\'</strong>)\n            ->join(<strong>\'role_has_permissions AS rp\'</strong>,  <strong>\'rp.role_id\'</strong>, <strong>\'=\'</strong>, <strong>\'ur.role_id\'</strong>)\n            ->join(<strong>\'permissions AS p\'</strong>,  <strong>\'p.id\'</strong>, <strong>\'=\'</strong>, <strong>\'rp.permission_id\'</strong>)\n            ->where([\n                <strong>\'ur.model_id\' </strong>=> $userId,\n                <strong>\'p.route\' </strong>=> $route\n            ])\n            ->exists();\n        <strong>if </strong>($hasRolePermission){\n            <strong>return true</strong>;\n        }\n        <em>// 查询用户是否直接拥有路由权限\n        </em>$hasUserPermission = DB::<strong>table</strong>(<strong>\'model_has_permissions AS up\'</strong>)\n            ->join(<strong>\'permissions AS p\'</strong>,  <strong>\'p.id\'</strong>, <strong>\'=\'</strong>, <strong>\'up.permission_id\'</strong>)\n            ->where([\n                <strong>\'up.model_id\' </strong>=> $userId,\n                <strong>\'p.route\' </strong>=> $route\n            ])\n            ->exists();\n        <strong>return </strong>$hasRolePermission || $hasUserPermission;\n    }\n}\n</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>3. 控制器逻辑</h4>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">3.1 OrganizationController\n------------------------------------------------\n<strong>&lt;?php\n</strong><em>/**\n * Created by PhpStorm.\n * User: WEN\n * Date: 2019/3/14\n * Time: 16:52\n */\n\n</em><strong>namespace </strong>App\\Http\\Controllers\\Admin;\n\n\n<strong>use </strong>App\\Http\\Controllers\\Controller;\n<strong>use </strong>App\\Models\\OrganizationModel;\n<strong>use </strong>App\\Models\\OrgUserModel;\n<strong>use </strong>App\\Models\\User;\n<strong>use </strong>App\\Service\\OrganizationService;\n<strong>use </strong>Illuminate\\Support\\Facades\\Request;\n<strong>use </strong>Illuminate\\Support\\Facades\\Validator;\n\n<strong>class </strong>OrganizationController <strong>extends </strong>Controller\n{\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>返回所有组织架构\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>index(){\n        <em>// 1. 获取所有组织\n        </em>$list = OrganizationModel::<em>get</em>([<strong>\'id\'</strong>,<strong>\'parent_id\'</strong>,<strong>\'level\'</strong>,<strong>\'name\'</strong>,<strong>\'en_name\'</strong>,<strong>\'icon_style\'</strong>,<strong>\'description\'</strong>,<strong>\'path\'</strong>])->toArray();\n\n        <em>// 2. 处理返回树\n        </em><strong>return </strong>$this->toSuccess(OrganizationService::<em>tree</em>($list));\n\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>新建组织部门\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>add()\n    {\n        <em>// 1. 获取请求数据 并校验\n        </em>$req = $this->getJsonContent();\n        $rules = [\n            <strong>\'parent_id\'  </strong>=> <strong>\'required|integer\'</strong>,\n            <strong>\'name\'  </strong>=> <strong>\'required\'</strong>,\n            <strong>\'en_name\'  </strong>=> <strong>\'required\'</strong>,\n            <strong>\'level\'  </strong>=> <strong>\'required|integer\'</strong>,\n            <strong>\'description\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'icon_style\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'path\'  </strong>=> <strong>\'required|string\'\n        </strong>];\n        $validator = Validator::<em>make</em>($req,$rules);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 创建数据入库\n        </em><strong>try</strong>{\n            $res = OrganizationModel::<em>create</em>($req);\n        }<strong>catch </strong>(\\PDOException $e){\n            <strong>return </strong>$this->toError($e->getMessage());\n        }\n\n\n        <strong>return </strong>$this->toSuccess($res);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>删除组织\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse|mixed\n     * </em><strong><em>@throws </em></strong><em>\\Exception\n     */\n    </em><strong>public function </strong>del()\n    {\n        <em>// 1. 获取请求内容 并校验\n        </em>$req = $this->getJsonContent();\n        $id = $req[<strong>\'id\'</strong>];\n        $validator = Validator::<em>make</em>($req,[<strong>\'id\'</strong>=><strong>\'required|integer\'</strong>]);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 检测是否存在子组织\n        </em><strong>if </strong>(OrganizationModel::<em>where</em>(<strong>\'parent_id\'</strong>,$id)->first()){\n            <strong>return </strong>$this->toError(<strong>\'存在子组织禁止删除\'</strong>);\n        }\n\n        <em>// 3. 执行删除\n        </em>\\DB::<strong>beginTransaction</strong>();\n        <strong>try</strong>{\n            <em>// 1. 删除组织\n            </em>OrganizationModel::<em>destroy</em>($id);\n\n            <em>// 2. 删除组织下的用户关系\n            </em>OrgUserModel::<em>where</em>(<strong>\'org_id\'</strong>,$id)->delete();\n\n            \\DB::<strong>commit</strong>();\n        }<strong>catch </strong>(\\PDOException $e){\n            \\DB::<strong>rollBack</strong>();\n            <strong>return </strong>$this->toError(<strong>\'删除组织失败\'</strong>);\n        }\n\n        <strong>return </strong>$this->toSuccess(<strong>\'删除组织成功\'</strong>);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>更新组织\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>edit()\n    {\n        <em>// 1. 获取前端请求数据  并校验\n        </em>$req = Request::<em>only</em>([<strong>\'id\'</strong>,<strong>\'name\'</strong>,<strong>\'en_name\'</strong>,<strong>\'icon_style\'</strong>,<strong>\'description\'</strong>,<strong>\'level\'</strong>]);\n        $rules = [\n            <strong>\'id\' </strong>=> <strong>\'required|integer\'</strong>,\n            <strong>\'name\'</strong>=><strong>\'required|string\'</strong>,\n            <strong>\'en_name\'  </strong>=> <strong>\'required|string\'</strong>,\n            <strong>\'icon_style\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'description\'  </strong>=> <strong>\'string\'</strong>,\n            <strong>\'level\'  </strong>=> <strong>\'integer\'</strong>,\n            <strong>\'path\'  </strong>=> <strong>\'string\'</strong>,\n        ];\n        $validator = Validator::<em>make</em>($req,$rules);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 获取模型\n        </em>$row = OrganizationModel::<em>findOrFail</em>($req[<strong>\'id\'</strong>]);\n\n        <em>// 3. 执行更新\n        </em>$bol = $row->update($req);\n        <strong>if </strong>(<strong>false </strong>== $bol)\n            <strong>return </strong>$this->toError(<strong>\'更新组织失败\'</strong>);\n\n        <strong>return </strong>$this->toSuccess(<strong>\'更新组织成功\'</strong>);\n    }\n\n\n}\n\n3.2 OrganizationHasUsersController\n----------------------------------------------\n<strong>&lt;?php\n</strong><em>/**\n * Created by PhpStorm.\n * User: WEN\n * Date: 2019/3/14\n * Time: 16:52\n */\n\n</em><strong>namespace </strong>App\\Http\\Controllers\\Admin;\n\n\n<strong>use </strong>App\\Http\\Controllers\\Controller;\n<strong>use </strong>App\\Models\\OrganizationModel;\n<strong>use </strong>Illuminate\\Http\\Request;\n<strong>use </strong>Illuminate\\Support\\Facades\\Validator;\n\n<strong>class </strong>OrganizationHasUsersController <strong>extends </strong>Controller\n{\n    <strong>protected $req_fields </strong>= [\n        <strong>\'org_id\'  </strong>=> <strong>\'required|integer\'</strong>,\n        <strong>\'user_ids\'  </strong>=> <strong>\'array\'\n    </strong>];\n\n    <strong>protected $msgs </strong>= [\n        <strong>\'org_id.required\'</strong>=><strong>\'组织标识必传\'</strong>,\n        <strong>\'org_id.integer\'</strong>=><strong>\'组织标识必须是整数\'</strong>,\n        <strong>\'org_id.array\'</strong>=><strong>\'用户标识必须为数组\'</strong>,\n    ];\n\n    <em>/**\n     * </em><strong><em>@desc   </em></strong><em>返回组织和用户的关系列表\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse\n     */\n    </em><strong>public function </strong>index(Request $request){\n        <em>// 1. 获取前端请求数据 并校验\n        </em>$req = $request->only(<strong>\'id\'</strong>);\n        $validator = Validator::<em>make</em>($req, [<strong>\'id\'  </strong>=> <strong>\'required|integer\'</strong>],[<strong>\'id.required\'</strong>=><strong>\'组织标识必传\'</strong>,<strong>\'id.integer\'</strong>=><strong>\'组织标识必须是整数\'</strong>]);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 获取组织和用户关系\n        </em>$users = OrganizationModel::<em>getOrgHaveUsers</em>($req[<strong>\'id\'</strong>]);\n\n        <em>// 4. 响应前端\n        </em><strong>return </strong>$this->toSuccess($users);\n    }\n\n    <em>/**\n     * </em><strong><em>@desc  </em></strong><em>组织绑定用户\n     * </em><strong><em>@param </em></strong><em>Request $request\n     * </em><strong><em>@return </em></strong><em>\\Illuminate\\Http\\JsonResponse|mixed\n     */\n    </em><strong>public function </strong>bindUsers(Request $request)\n    {\n        <em>// 1. 获取请求数据 并校验\n        </em>$req = $request->only(<em>array_keys</em>($this-><strong>req_fields</strong>));\n        $validator = Validator::<em>make</em>($req,$this-><strong>req_fields</strong>,$this-><strong>msgs</strong>);\n        <strong>if </strong>($validator->fails()) {\n            <strong>return </strong>$this->toError($validator->errors()->first());\n        }\n\n        <em>// 2. 获取当前组织下原绑定的用户\n        </em><strong>try</strong>{\n            $res = OrganizationModel::<em>bindUsers</em>($req[<strong>\'org_id\'</strong>],$req[<strong>\'user_ids\'</strong>]);\n        }<strong>catch </strong>(\\PDOException $e){\n            <strong>return </strong>$this->toError($e->getMessage());\n        }\n\n\n        <strong>return </strong>$this->toSuccess($res);\n    }\n\n}</pre>\n<!-- /wp:preformatted -->','PHP+MYSQL无限极分类','','inherit','closed','closed','','233-revision-v1','','','2019-07-23 17:30:04','2019-07-23 09:30:04','',233,'http://long521.cn/2019/07/23/233-revision-v1/',0,'revision','',0),(238,1,'2019-07-26 16:32:48','2019-07-26 08:32:48','<!-- wp:list -->\n<ul><li>无需修改hosts</li><li>无需修改jar包</li><li>直接切换到activity code选项粘贴一下激活码</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:image {\"id\":239} -->\n<figure class=\"wp-block-image\"><img src=\"http://long521.cn/wp-content/upload